    private void x2ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_x2ButtonActionPerformed
        // TODO add your handling code here:
        if(jTextField1.getText().length()>0){
            calculatesvalue = Double.parseDouble(jTextField1.getText());
            square(calculatesvalue);
        }
    }//GEN-LAST:event_x2ButtonActionPerformed
    private void square(double a){
        jTextField1.setText(Double.toString(a*=a));
    }
    
    private void backspaceButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_backspaceButtonActionPerformed
        // TODO add your handling code here:
        int length = jTextField1.getText().length();
        String value = jTextField1.getText();
        if(length>0){
            StringBuilder builder = new StringBuilder(value);
            builder.deleteCharAt(length-1);
            jTextField1.setText(builder.toString());
        }
    }//GEN-LAST:event_backspaceButtonActionPerformed

