    private void oneByX(double a){
       jTextField1.setText(Double.toString(1/a));
    }
    private void sqrt(double a){
        jTextField1.setText(Double.toString(Math.sqrt(a)));
    }
    private void percentage(double a, double b){
        switch(sign){
            case "+":
                add(((a*b)/100),a);
                break;
            case "-":
                    sub(a,((a*b)/100));
                    break;
            case "*":
                    mul(((a*b)/100),a);
                    break;
            case "/":
                    div(((a*b)/100),a);
                    break;
        }
    }
